<div class="sidebar-filter">
    <!-- Categories Product -->
    <section id="categories-product">
        <h4 class="widget-title">Categories</h4>
        <nav aria-label="Categories">
            <ul class="category-list">
                <?php
                // PHP logic để lấy danh mục và số lượng sản phẩm
                $categories = get_terms([
                    'taxonomy' => 'product_cat',
                    'hide_empty' => false,
                ]);

                if (!empty($categories) && !is_wp_error($categories)):
                    foreach ($categories as $category): ?>
                        <li class="cat-item">
                            <a href="<?php echo esc_url(get_term_link($category)); ?>">
                                <?php echo esc_html($category->name); ?>
                            </a>
                            <span class="product-count"><?php echo esc_html($category->count); ?></span>
                        </li>
                        <?php
                    endforeach;
                endif;
                ?>
            </ul>
        </nav>
    </section>

    <!-- Form lọc sản phẩm -->
    <form method="GET">
        <!-- Thêm sắp xếp -->
        <input type="hidden" name="orderby" value="<?php echo isset($_GET['orderby']) ? $_GET['orderby'] : '' ?>">

        <?php
        $selected_tags = isset($_GET['product_tags']) ? $_GET['product_tags'] : [];

        $tags = get_terms([
            'taxonomy' => 'product_tag',
            'hide_empty' => false,
        ]);

        if (!empty($tags)):
            ?>
            <div class="product-tags-checkboxes">
                <h3>Tags</h3>
                <?php
                foreach ($tags as $tag):
                    $checked = in_array($tag->term_id, $selected_tags) ? 'checked' : '';
                    ?>
                    <label>
                        <input type="checkbox" name="product_tags[]" value="<?php echo esc_attr($tag->term_id); ?>" <?php echo $checked; ?>>
                        <?php echo esc_html($tag->name); ?>
                        (<?php echo $tag->count ?? 0; ?>)
                    </label>
                    <?php
                endforeach;
                ?>
            </div>
            <?php
        endif;
        ?>
        <?php $rating = isset($_GET['rating']) ? intval($_GET['rating']) : 0 ?>
